SELECT gl.main_picture AS mainPicture, gl.id, gl.`status`, gl.good_name AS goodName, gt.`parent_id` AS typeTwo, gtt.`parent_id` AS typeOne, gl.`good_type_id` AS typeThree, gl.audit_status as auditStatus, ( SELECT cost_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY cost_price ASC LIMIT 1 ) AS minCostPrice, ( SELECT cost_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY cost_price DESC LIMIT 1 ) AS maxCostPrice, ( SELECT vip_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY vip_price ASC LIMIT 1 ) AS minVipPrice, ( SELECT vip_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY vip_price DESC LIMIT 1 ) AS maxVipPrice, ( SELECT vip_two_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY vip_price ASC LIMIT 1 ) AS minVipTwoPrice, ( SELECT vip_two_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY vip_price DESC LIMIT 1 ) AS maxVipTwoPrice, ( SELECT vip_third_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY vip_price ASC LIMIT 1 ) AS minVipThirdPrice, ( SELECT vip_third_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY vip_price DESC LIMIT 1 ) AS maxVipThirdPrice, ( SELECT vip_fouth_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY vip_price ASC LIMIT 1 ) AS minVipFouthPrice, ( SELECT vip_fouth_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY vip_price DESC LIMIT 1 ) AS maxVipFouthPrice, ( SELECT supply_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY supply_price ASC LIMIT 1 ) AS minSupplyPrice, ( SELECT supply_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY supply_price DESC LIMIT 1 ) AS maxSupplyPrice, ( SELECT price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY price ASC LIMIT 1 ) AS minPrice, ( SELECT price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY price DESC LIMIT 1 ) AS maxPrice, gl.create_by AS createBy, DATE_FORMAT( gl.`create_time`, '%Y-%m-%d %H:%i:%s' ) AS createTime, ( SELECT SUM(sales_volume) FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ) AS salesVolume, ( SELECT CONCAT( gtThree.NAME, '-', gtTwo.NAME, '-', gtOne.NAME ) FROM good_type gtOne LEFT JOIN good_type gtTwo ON gtTwo.id = gtOne.parent_id LEFT JOIN good_type gtThree ON gtThree.id = gtTwo.parent_id WHERE gtOne.id = gl.`good_type_id` ) AS goodTypeName, gl.is_specification AS isSpecification, gl.status_explain AS statusExplain, gl.good_no AS goodNo, gl.details_goods as detailsGoods, gl.commitment_customers as commitmentCustomers, gl.specifications, gl.specification, gl.sys_user_id as sysUserId, gl.provider_template_id as providerTemplateId, gl.market_price as marketPrice, gl.good_brand_id as goodBrandId, gl.good_machine_brand_ids as goodMachineBrandIds, gl.good_machine_model_ids as goodMachineModelIds, gl.frame_status as frameStatus, gl.good_describe as goodDescribe FROM (select * from `good_list`${ew.customSqlSegment} ) gl LEFT JOIN `good_type` gt ON gl.`good_type_id` = gt.`id` LEFT JOIN `good_type` gtt ON gt.`parent_id` = gtt.`id` WHERE gl.del_flag = '0'
and gtt.`parent_id`=#{paramMap.typeId}
and gt.`parent_id`=#{paramMap.typeId}
and gl.`good_type_id`=#{paramMap.typeId}
ORDER BY gl.create_time DESC

SELECT gl.*, goodTypes.goodTypeIdThree AS goodTypeIdThree, goodTypes.goodTypeIdTwo AS goodTypeIdTwo, goodTypes.goodTypeIdOne AS goodTypeIdOne, goodTypes.goodTypeThreeName AS goodTypeThreeName, goodTypes.goodTypeTwoName AS goodTypeTwoName, goodTypes.goodTypeOneName AS goodTypeOneName, CONCAT_WS( '-', goodTypes.goodTypeOneName, goodTypes.goodTypeTwoName, goodTypes.goodTypeThreeName ) AS goodTypeNames, su.realname AS realname, (SELECT good_audit FROM provider_manage WHERE sys_user_id =su.id AND del_flag = 0) AS goodAudit, gss.discount AS discount, gss.minDiscount AS minDiscount, gss.maxDiscount AS maxDiscount FROM good_list gl LEFT JOIN (SELECT gt.id, gt.parent_id, gt.id AS goodTypeIdThree, gt.name AS goodTypeThreeName, gttwo.id AS goodTypeIdTwo, gttwo.name AS goodTypeTwoName, gttwo.goodTypeIdOne AS goodTypeIdOne, gttwo.goodTypeOneName AS goodTypeOneName FROM good_type gt LEFT JOIN (SELECT gttwo.id, gttwo.parent_id, gttwo.name, gtOne.id AS goodTypeIdOne, gtOne.name AS goodTypeOneName FROM good_type gttwo LEFT JOIN good_type gtOne ON gttwo.parent_id = gtOne.id) gttwo ON gt.parent_id = gttwo.id) goodTypes ON gl.good_type_id = goodTypes.id LEFT JOIN `sys_user` su ON su.id = gl.sys_user_id LEFT JOIN (SELECT gs.good_list_id AS good_list_id, IF( MAX(CEIL(gs.cost_price/gs.price*10000)/100 ) = MIN(CEIL(gs.cost_price/gs.price*10000)/100), CONCAT(ROUND(MIN(CEIL(gs.cost_price/gs.price*10000)/100 ),2),'%'), CONCAT_WS( '-',CONCAT(ROUND( MIN(CEIL(gs.cost_price/gs.price*10000)/100),2),'%') ,CONCAT(ROUND( MAX(CEIL(gs.cost_price/gs.price*10000)/100),2),'%')) ) AS discount, ROUND(MIN(CEIL(gs.cost_price/gs.price*10000)/100 ),2) AS minDiscount, ROUND(MAX(CEIL(gs.cost_price/gs.price*10000)/100 ),2) AS maxDiscount FROM `good_specification` gs WHERE gs.del_flag = 0 GROUP BY gs.good_list_id) gss ON gss.good_list_id = gl.`id` WHERE gl.del_flag = 0
AND gl.audit_status != 0
AND gl.good_type_id =#{goodListVo.goodTypeId}
AND goodTypeIdThree =#{goodListVo.goodTypeIdThree}
AND goodTypeIdTwo =#{goodListVo.goodTypeIdTwo}
AND goodTypeIdOne =#{goodListVo.goodTypeIdOne}
AND gl.good_name LIKE CONCAT(CONCAT('%',#{goodListVo.goodName}), '%')
AND realname LIKE CONCAT(CONCAT('%',#{goodListVo.realname}), '%')
AND gl.good_no LIKE CONCAT(CONCAT('%',#{goodListVo.goodNo}), '%')
AND gl.sys_user_id =#{goodListVo.sysUserId}
AND gl.frame_status =#{goodListVo.frameStatus}
AND gl.audit_status =#{goodListVo.auditStatus}
AND gl.status =#{goodListVo.status}
AND gl.activities_type =#{goodListVo.activitiesType}
AND gl.del_flag =#{goodListVo.delFlag}
and gl.create_time >= concat(#{goodListVo.createTime_begin},' 00:00:00')
AND gl.create_time <= concat( #{goodListVo.createTime_end},' 23:59:59')
and minDiscount >= #{goodListVo.minDiscount}
and maxDiscount < #{goodListVo.maxDiscount}
ORDER BY gl.create_time DESC

SELECT gl.*, goodTypes.goodTypeIdThree AS goodTypeIdThree, goodTypes.goodTypeIdTwo AS goodTypeIdTwo, goodTypes.goodTypeIdOne AS goodTypeIdOne, goodTypes.goodTypeThreeName AS goodTypeThreeName, goodTypes.goodTypeTwoName AS goodTypeTwoName, goodTypes.goodTypeOneName AS goodTypeOneName, CONCAT_WS( '-', goodTypes.goodTypeOneName, goodTypes.goodTypeTwoName, goodTypes.goodTypeThreeName ) AS goodTypeNames, su.realname AS realname FROM good_list gl LEFT JOIN (SELECT gt.id, gt.parent_id, gt.id AS goodTypeIdThree, gt.name AS goodTypeThreeName, gttwo.id AS goodTypeIdTwo, gttwo.name AS goodTypeTwoName, gttwo.goodTypeIdOne AS goodTypeIdOne, gttwo.goodTypeOneName AS goodTypeOneName FROM good_type gt LEFT JOIN (SELECT gttwo.id, gttwo.parent_id, gttwo.name, gtOne.id AS goodTypeIdOne, gtOne.name AS goodTypeOneName FROM good_type gttwo LEFT JOIN good_type gtOne ON gttwo.parent_id = gtOne.id) gttwo ON gt.parent_id = gttwo.id) goodTypes ON gl.good_type_id = goodTypes.id LEFT JOIN `sys_user` su ON su.id = gl.sys_user_id WHERE gl.del_flag = 1
AND goodTypeIdThree =#{goodListVo.goodTypeId}
AND goodTypeIdThree =#{goodListVo.goodTypeIdThree}
AND goodTypeIdTwo =#{goodListVo.goodTypeIdTwo}
AND goodTypeIdOne =#{goodListVo.goodTypeIdOne}
AND gl.good_name LIKE CONCAT(CONCAT('%',#{goodListVo.goodName}), '%')
AND gl.good_no LIKE CONCAT(CONCAT('%',#{goodListVo.goodNo}), '%')
AND gl.sys_user_id =#{goodListVo.sysUserId}
AND gl.frame_status =#{goodListVo.frameStatus}
AND gl.audit_status =#{goodListVo.auditStatus}
AND gl.status =#{goodListVo.status}
AND gl.activities_type =#{goodListVo.activitiesType}
and gl.create_time >= concat(#{goodListVo.createTime_begin},' 00:00:00')
AND gl.create_time <= concat( #{goodListVo.createTime_end},' 23:59:59')
and gl.id in
#{string}
AND realname LIKE CONCAT(CONCAT('%',#{goodListVo.realname}), '%')
ORDER BY gl.create_time DESC

SELECT * FROM ( SELECT gl.good_no AS goodNo, gl.main_picture AS mainPicture, gl.good_name AS goodName, ( SELECT supply_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY supply_price ASC LIMIT 1 ) AS minSupplyPrice, ( SELECT supply_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY supply_price DESC LIMIT 1 ) AS maxSupplyPrice, ( SELECT price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY price ASC LIMIT 1 ) AS minPrice, ( SELECT price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY price DESC LIMIT 1 ) AS maxPrice, ( SELECT SUM( sales_volume ) FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ) AS salesVolume, ( SELECT SUM( repertory ) FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ) AS repertory, ( SELECT COUNT( 1 ) FROM `marketing_free_good_list` mfgl WHERE mfgl.good_list_id = gl.`id` AND mfgl.del_flag = '0' ) AS marketingFreeGoodListCount, ( SELECT COUNT( 1 ) FROM `marketing_league_good_list` mfgl WHERE mfgl.good_list_id = gl.`id` AND mfgl.del_flag = '0' ) AS marketingLeagueGoodListCount, ( SELECT COUNT( 1 ) FROM `marketing_rush_good` mfgl WHERE mfgl.good_list_id = gl.`id` AND mfgl.del_flag = '0' ) AS marketingRushGoodCount, ( SELECT CONCAT( gtThree.NAME, '-', gtTwo.NAME, '-', gtOne.NAME ) FROM good_type gtOne LEFT JOIN good_type gtTwo ON gtTwo.id = gtOne.parent_id LEFT JOIN good_type gtThree ON gtThree.id = gtTwo.parent_id WHERE gtOne.id = gl.`good_type_id` ) AS goodTypeName, gl.`market_price` AS marketPrice, gl.is_specification AS isSpecification, gl.`id`, ( SELECT NAME FROM `provider_manage` WHERE sys_user_id = gl.`sys_user_id` AND del_flag = '0' AND STATUS = '1' ) AS sysUserName, ( SELECT TRUNCATE ( cost_price / price, 2 ) * 100 AS discount FROM `good_specification` WHERE good_list_id = gl.`id` ORDER BY discount ASC LIMIT 1 ) AS discountStart, ( SELECT TRUNCATE ( cost_price / price, 2 ) * 100 AS discount FROM `good_specification` WHERE good_list_id = gl.`id` ORDER BY discount DESC LIMIT 1 ) AS discountEnd, gl.`good_type_id` AS goodTypeId FROM good_list gl WHERE gl.del_flag = '0' AND gl.STATUS = '1' AND gl.frame_status = '1' AND gl.audit_status = '2' ORDER BY minPrice ASC, salesVolume, gl.update_time DESC ) gg WHERE 1 = 1
and gg.goodNo=#{paramMap.goodNo}
and gg.goodName like concat('%',#{paramMap.goodName},'%')
and gg.goodTypeId=#{paramMap.goodTypeId}
and gg.discountStart >=#{paramMap.discountStart}
and gg.discountEnd <=#{paramMap.discountEnd}
and gg.sysUserName like concat('%',#{paramMap.sysUserName},'%')
and gg.marketingFreeGoodListCount=0
and gg.marketingFreeGoodListCount >0
and gg.marketingLeagueGoodListCount=0
and gg.marketingLeagueGoodListCount >0
and gg.marketingRushGoodCount =0 and gg.isSpecification='0'
and gg.discountStart=gg.discountEnd

SELECT gl.id AS id, gl.good_name AS goodName, CONCAT( ( SELECT price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY price ASC LIMIT 1 ), '-',( SELECT price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY price DESC LIMIT 1 )) AS price, CONCAT(( SELECT cost_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY cost_price ASC LIMIT 1 ), '-',( SELECT cost_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY cost_price DESC LIMIT 1 ) ) AS costPrice, gl.market_price AS marketPrice, gl.main_picture AS mainPicture, ( SELECT SUM( repertory ) FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ) AS repertory, goodTypes.goodTypeIdThree AS goodTypeIdThree, goodTypes.goodTypeIdTwo AS goodTypeIdTwo, goodTypes.goodTypeIdOne AS goodTypeIdOne, goodTypes.goodTypeThreeName AS goodTypeThreeName, goodTypes.goodTypeTwoName AS goodTypeTwoName, goodTypes.goodTypeOneName AS goodTypeOneName, CONCAT_WS( '-', goodTypes.goodTypeOneName, goodTypes.goodTypeTwoName, goodTypes.goodTypeThreeName ) AS goodTypeNames, su.realname AS realname, gl.frame_status AS frameStatus, gl.status AS status, gss.discount AS discount, gss.minDiscount AS minDiscount, gss.maxDiscount AS maxDiscount FROM good_list gl LEFT JOIN (SELECT gt.id, gt.parent_id, gt.id AS goodTypeIdThree, gt.name AS goodTypeThreeName, gttwo.id AS goodTypeIdTwo, gttwo.name AS goodTypeTwoName, gttwo.goodTypeIdOne AS goodTypeIdOne, gttwo.goodTypeOneName AS goodTypeOneName, gt.`status` AS goodTypeThreeStatus, gttwo.goodTypeTwoStatus AS goodTypeTwoStatus, gttwo.goodTypeOneStatus AS goodTypeOneStatus FROM good_type gt LEFT JOIN (SELECT gttwo.id, gttwo.parent_id, gttwo.name, gtOne.id AS goodTypeIdOne, gtOne.name AS goodTypeOneName, gttwo.`status` AS goodTypeTwoStatus, gtOne.`status` AS goodTypeOneStatus FROM good_type gttwo LEFT JOIN good_type gtOne ON gttwo.parent_id = gtOne.id) gttwo ON gt.parent_id = gttwo.id) goodTypes ON gl.good_type_id = goodTypes.id LEFT JOIN `sys_user` su ON su.id = gl.sys_user_id LEFT JOIN (SELECT gs.good_list_id AS good_list_id, IF( MAX((gs.cost_price/gs.price*10000)/100 ) = MIN((gs.cost_price/gs.price*10000)/100), CONCAT(ROUND(MIN(CEIL(gs.cost_price/gs.price*10000)/100 ),2),'%'), CONCAT_WS( '-',CONCAT(ROUND( MIN((gs.cost_price/gs.price*10000)/100),2),'%') ,CONCAT(ROUND( MAX((gs.cost_price/gs.price*10000)/100),2),'%')) ) AS discount, ROUND(MIN((gs.cost_price/gs.price*10000)/100 ),2) AS minDiscount, ROUND(MAX((gs.cost_price/gs.price*10000)/100 ),2) AS maxDiscount FROM `good_specification` gs WHERE gs.del_flag = 0 GROUP BY gs.good_list_id) gss ON gss.good_list_id = gl.`id` WHERE gl.del_flag = 0 AND gl.frame_status = 1 AND gl.status= 1 AND gl.audit_status = 2 AND gl.main_picture IS NOT NULL AND goodTypes.goodTypeThreeStatus = 1 AND goodTypes.goodTypeTwoStatus =1 AND goodTypes.goodTypeTwoStatus = 1
AND maxDiscount < #{goodListVo.astrictPriceProportion}
AND gl.good_name LIKE CONCAT(CONCAT('%',#{goodListVo.goodName}), '%')
AND realname LIKE CONCAT(CONCAT('%',#{goodListVo.realname}), '%')
AND goodTypeIdThree =#{goodListVo.goodTypeIdThree}
AND goodTypeIdTwo =#{goodListVo.goodTypeIdTwo}
AND goodTypeIdOne =#{goodListVo.goodTypeIdOne}
and gl.id not in
#{marketingPrefectureGoodNotId}
and gl.id in
#{marketingPrefectureGoodId}
and minDiscount >= #{goodListVo.minDiscount}
and maxDiscount <= #{goodListVo.maxDiscount}
AND gl.id =#{goodListVo.id}
ORDER by gl.create_time desc

SELECT gl.id AS id, gl.good_name AS goodName, CONCAT( ( SELECT price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY price ASC LIMIT 1 ), '-',( SELECT price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY price DESC LIMIT 1 )) AS price, CONCAT(( SELECT cost_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY cost_price ASC LIMIT 1 ), '-',( SELECT cost_price FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ORDER BY cost_price DESC LIMIT 1 ) ) AS costPrice, gl.market_price AS marketPrice, gl.main_picture AS mainPicture, ( SELECT SUM( repertory ) FROM good_specification WHERE good_list_id = gl.id AND del_flag = '0' ) AS repertory, goodTypes.goodTypeIdThree AS goodTypeIdThree, goodTypes.goodTypeIdTwo AS goodTypeIdTwo, goodTypes.goodTypeIdOne AS goodTypeIdOne, goodTypes.goodTypeThreeName AS goodTypeThreeName, goodTypes.goodTypeTwoName AS goodTypeTwoName, goodTypes.goodTypeOneName AS goodTypeOneName, CONCAT_WS( '-', goodTypes.goodTypeOneName, goodTypes.goodTypeTwoName, goodTypes.goodTypeThreeName ) AS goodTypeNames, su.realname AS realname, gl.frame_status AS frameStatus, gl.status AS status FROM good_list gl LEFT JOIN (SELECT gt.id, gt.parent_id, gt.id AS goodTypeIdThree, gt.name AS goodTypeThreeName, gttwo.id AS goodTypeIdTwo, gttwo.name AS goodTypeTwoName, gttwo.goodTypeIdOne AS goodTypeIdOne, gttwo.goodTypeOneName AS goodTypeOneName, gt.`status` AS goodTypeThreeStatus, gttwo.goodTypeTwoStatus AS goodTypeTwoStatus, gttwo.goodTypeOneStatus AS goodTypeOneStatus FROM good_type gt LEFT JOIN (SELECT gttwo.id, gttwo.parent_id, gttwo.name, gtOne.id AS goodTypeIdOne, gtOne.name AS goodTypeOneName, gttwo.`status` AS goodTypeTwoStatus, gtOne.`status` AS goodTypeOneStatus FROM good_type gttwo LEFT JOIN good_type gtOne ON gttwo.parent_id = gtOne.id) gttwo ON gt.parent_id = gttwo.id) goodTypes ON gl.good_type_id = goodTypes.id LEFT JOIN `sys_user` su ON su.id = gl.sys_user_id
WHERE gl.del_flag = 0 AND gl.frame_status = 1 AND gl.status= 1 AND gl.audit_status = 2 AND gl.main_picture IS NOT NULL AND goodTypes.goodTypeThreeStatus = 1 AND goodTypes.goodTypeTwoStatus =1 AND goodTypes.goodTypeTwoStatus = 1
AND gl.good_name LIKE CONCAT(CONCAT('%',#{goodListVo.goodName}), '%')
AND realname LIKE CONCAT(CONCAT('%',#{goodListVo.realname}), '%')
AND goodTypeIdThree =#{goodListVo.goodTypeIdThree}
AND goodTypeIdTwo =#{goodListVo.goodTypeIdTwo}
AND goodTypeIdOne =#{goodListVo.goodTypeIdOne}
and gl.id not in
#{marketingPrefectureGoodNotId}
and gl.id in
#{marketingPrefectureGoodId}
ORDER BY gl.`create_time` DESC

SELECT gl.id AS id,gl.main_picture AS mainPicture,1 AS isPlatform,gl.good_name AS goodName,gl.small_price AS smallPrice,gl.small_vip_price AS smallVipPrice,gl.activities_type AS activitiesType,gl.activity_price AS activityPrice, (SELECT COUNT(1) FROM marketing_discount_good mg LEFT JOIN marketing_discount ms ON mg.marketing_discount_id = ms.id WHERE ms.status = 1 AND ms.del_flag = 0 AND ms.total > ms.released_quantity AND mg.good_id = gl.id AND mg.is_platform='1') AS discount, 1 AS isViewVipPrice, source_type AS sourceType, mdgs.COUNT AS marketingDiscountGoodCount FROM good_list gl LEFT JOIN (SELECT gt.id, gt.parent_id, gt.id AS goodTypeIdThree, gt.name AS goodTypeThreeName, gttwo.id AS goodTypeIdTwo, gttwo.name AS goodTypeTwoName, gttwo.goodTypeIdOne AS goodTypeIdOne, gttwo.goodTypeOneName AS goodTypeOneName FROM good_type gt LEFT JOIN (SELECT gttwo.id, gttwo.parent_id, gttwo.name, gtOne.id AS goodTypeIdOne, gtOne.name AS goodTypeOneName FROM good_type gttwo LEFT JOIN good_type gtOne ON gttwo.parent_id = gtOne.id) gttwo ON gt.parent_id = gttwo.id) goodTypes ON gl.good_type_id = goodTypes.id LEFT JOIN (SELECT mdg.good_id,COUNT(0) AS COUNT FROM marketing_discount_good mdg WHERE mdg.`del_flag` = '0' AND mdg.is_platform = 1 GROUP BY mdg.good_id) mdgs ON mdgs.good_id = gl.id WHERE del_flag = '0' AND STATUS=1 AND frame_status=1 AND audit_status ='2' AND main_picture IS NOT NULL and gl.good_name like concat('%',#{searchTermsVO.search},'%') or gl.good_describe like concat('%',#{searchTermsVO.search},'%')
and goodTypes.goodTypeIdOne =#{searchTermsVO.goodTypeId}
and goodTypes.goodTypeIdTwo =#{searchTermsVO.goodTypeIdTwo}
and goodTypes.goodTypeIdThree =#{searchTermsVO.goodTypeIdThree}
and gl.small_price >= #{searchTermsVO.minPrice}
and gl.small_price <= #{searchTermsVO.maxPrice}
and mdgs.COUNT >= #{searchTermsVO.marketingDiscountGoodCount}
and gl.source_type =#{searchTermsVO.sourceType}
order by gl.small_price asc,gl.sales_volume,gl.update_time desc order by gl.sales_volume desc order by gl.update_time desc order by gl.small_price order by gl.small_price desc
