mapper.select:
select gl.good_no AS goodNo, gl.main_picture AS mainPicture, gl.good_name AS goodName, gl.price, gl.supply_price AS supplyPrice, 1 AS isPlatform, gl.repertory, (SELECT CONCAT( gtThree.name, '-', gtTwo.name, '-', gtOne.name ) FROM good_type gtOne LEFT JOIN good_type gtTwo ON gtTwo.id = gtOne.parent_id LEFT JOIN good_type gtThree ON gtThree.id = gtTwo.parent_id WHERE gtOne.id = gl.`good_type_id`) AS goodTypeName, gl.`market_price` AS marketPrice, gl.is_specification AS isSpecification, gl.`id` as goodListId, gl.`good_type_id` AS goodTypeId, mfgl.`is_recommend` as isRecommend, (select type_name from `marketing_free_good_type` where id = mfgl.`marketing_free_good_type_id`) as typeName, mfgl.`status`, mfgl.`status_explain` as statusExplain, DATE_FORMAT(mfgl.`create_time`,'%Y-%m-%d %H:%i:%s') as createTime, mfgl.`id`, mfgl.`create_by` as createBy, mfgl.`update_by` as updateBy, DATE_FORMAT(mfgl.`update_time`,'%Y-%m-%d %H:%i:%s') as updateTime, (select mfgs.free_price from `marketing_free_good_specification` mfgs left join good_specification gs on gs.id = mfgs.`good_specification_id` where mfgs.marketing_free_good_list_id = mfgl.`id` and mfgs.del_flag = '0' and gs.`del_flag` = '0' order by free_price ASC LIMIT 1) freePriceStart, (SELECT mfgs.free_price FROM `marketing_free_good_specification` mfgs LEFT JOIN good_specification gs ON gs.id = mfgs.`good_specification_id` WHERE mfgs.marketing_free_good_list_id = mfgl.`id` AND mfgs.del_flag = '0' AND gs.`del_flag` = '0' ORDER BY free_price DESC LIMIT 1) freePriceEnd, (SELECT mfgs.free_price_proportion FROM `marketing_free_good_specification` mfgs LEFT JOIN good_specification gs ON gs.id = mfgs.`good_specification_id` WHERE mfgs.marketing_free_good_list_id = mfgl.`id` AND mfgs.del_flag = '0' AND gs.`del_flag` = '0' ORDER BY free_price ASC LIMIT 1) freePriceProportionStart, (SELECT mfgs.free_price_proportion FROM `marketing_free_good_specification` mfgs LEFT JOIN good_specification gs ON gs.id = mfgs.`good_specification_id` WHERE mfgs.marketing_free_good_list_id = mfgl.`id` AND mfgs.del_flag = '0' AND gs.`del_flag` = '0' ORDER BY free_price DESC limit 1) freePriceProportionEnd, mfgl.`marketing_free_good_type_id` AS marketingFreeGoodTypeId, mfgl.sort from `marketing_free_good_list` mfgl left join good_list gl on gl.`id` = mfgl.`good_list_id` where mfgl.`del_flag` = '0' and gl.del_flag = '0' AND gl.status = '1' AND gl.frame_status = '1' AND gl.good_form = '0' AND gl.audit_status = '2'
and gl.good_no=#{paramMap.goodNo}
and gl.good_name like concat('%',#{paramMap.goodName},'%')
and gl.good_type_id=#{paramMap.goodTypeId}
and mfgl.marketing_free_good_type_id=#{paramMap.marketingFreeGoodTypeId}
and mfgl.create_time >=#{paramMap.createTimeStart}
and mfgl.create_time <=#{paramMap.createTimeEnd}
and mfgl.update_time >=#{paramMap.updateTimeStart}
and mfgl.update_time <=#{paramMap.updateTimeEnd}
and mfgl.is_recommend=#{paramMap.isRecommend}
and mfgl.status=#{paramMap.status}
order by mfgl.`create_time` desc

SELECT gl.main_picture AS mainPicture, gl.good_name AS goodName, gl.`market_price` AS marketPrice, gl.`id` AS goodListId, mfgl.`id`, (SELECT mfgs.free_price FROM `marketing_free_good_specification` mfgs LEFT JOIN good_specification gs ON gs.id = mfgs.`good_specification_id` WHERE mfgs.marketing_free_good_list_id = mfgl.`id` AND mfgs.del_flag = '0' AND gs.`del_flag` = '0' ORDER BY free_price ASC LIMIT 1) freePrice, (select label from marketing_free_base_setting limit 1) AS label FROM `marketing_free_good_list` mfgl LEFT JOIN good_list gl ON gl.`id` = mfgl.`good_list_id` WHERE mfgl.`del_flag` = '0' AND mfgl.`status`='1' AND gl.del_flag = '0' AND gl.status = '1' AND gl.frame_status = '1' AND gl.good_form = '0' AND gl.audit_status = '2' and (SELECT mfgs.free_price FROM `marketing_free_good_specification` mfgs LEFT JOIN good_specification gs ON gs.id = mfgs.`good_specification_id` WHERE mfgs.marketing_free_good_list_id = mfgl.`id` AND mfgs.del_flag = '0' AND gs.`del_flag` = '0' ORDER BY free_price ASC LIMIT 1) is not null AND mfgl.`marketing_free_good_type_id`=#{paramMap.marketingFreeGoodTypeId}
ORDER BY gl.small_price ASC, gl.sales_volume, gl.update_time DESC
ORDER BY mfgl.sort ASC
