mapper.select:
SELECT mmg.*, gl.main_picture AS mainPicture, gl.good_name AS goodName, gl.market_price AS marketPrice, gl.price AS price, gl.vip_price AS vipPrice, CONCAT_WS( '-', goodTypes.goodTypeOneName, goodTypes.goodTypeTwoName, goodTypes.goodTypeThreeName ) AS goodTypeNames, su.realname AS realname FROM `marketing_material_good` mmg LEFT JOIN `good_list` gl ON mmg.`good_list_id` = gl.`id` LEFT JOIN (SELECT gt.id, gt.parent_id, gt.id AS goodTypeIdThree, gt.name AS goodTypeThreeName, gttwo.id AS goodTypeIdTwo, gttwo.name AS goodTypeTwoName, gttwo.goodTypeIdOne AS goodTypeIdOne, gttwo.goodTypeOneName AS goodTypeOneName FROM good_type gt LEFT JOIN (SELECT gttwo.id, gttwo.parent_id, gttwo.name, gtOne.id AS goodTypeIdOne, gtOne.name AS goodTypeOneName FROM good_type gttwo LEFT JOIN good_type gtOne ON gttwo.parent_id = gtOne.id) gttwo ON gt.parent_id = gttwo.id) goodTypes ON gl.good_type_id = goodTypes.id LEFT JOIN `sys_user` su ON su.id = gl.sys_user_id WHERE mmg.`del_flag` = 0 AND gl.del_flag = 0
AND mmg.marketing_material_list_id = #{marketingMaterialListId}
ORDER BY mmg.create_time DESC
